import React, { useEffect, useState } from "react";
import { App, Space, Card, Image, Typography, Button } from "antd";
import { Link, useParams } from "react-router-dom";
import { getShowCharacter } from "../../../services/requests";
import Loading from "../../../components/Loading";
import { ArrowLeftOutlined } from "@ant-design/icons";
import { useMediaQuery } from "react-responsive";

const { Meta } = Card;
const { Text } = Typography;

function ShowPage() {
  const { id } = useParams();

  const isMobile = useMediaQuery({ query: "(max-width: 810px)" });

  const { notification } = App.useApp();

  const [character, setCharacter] = useState();
  const [loading, setLoading] = useState(false);

  async function showCharacter(id) {
    setLoading(true);
    try {
      const response = await getShowCharacter("character", id);
      setCharacter(response.data);
    } catch (error) {
      notification.error({
        message: "Error",
        description: "An unexpected error occurred!",
        placement: "topRight",
      });
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    showCharacter(id);
  }, []);
  if (loading) {
    return <Loading />;
  }

  return (
    <>
      <Link style={{ position: "absolute", left: isMobile ? 7 : 50, top: 110 }} to="/">
        <Button
          style={{
            backgroundColor: "#3ec9a7",
            color: "#fff",
            borderRadius: "50%",
            height: 60,
            width: 60,
          }}
        >
          <ArrowLeftOutlined style={{ fontSize: 20 }} />
        </Button>
      </Link>
      <Space
        direction={isMobile ? "vertical" : "horizontal"}
        style={{
          width: "80%",
          height: 500,
          justifyContent: "space-around",
          padding: "1rem",
          backgroundColor: "transparent",
          marginTop: isMobile ? 250 : 50,
        }}
      >
        <Card
          hoverable
          style={{ width: 300, margin: "0 auto" }}
          cover={
            <Image
              src={character?.image ? character?.image : "error"}
              fallback="data:image/png;base64,"
            />
          }
        >
          <Meta title={character?.name} />
        </Card>
        <Card
          style={{
            width: isMobile ? 300: 500,
            height: isMobile ? 500 : 400,
            backgroundColor: "#ffffff",
            borderRadius: "6px",
            border: "1px solid #fff",
            boxShadow: "4px 6px 15px -3px rgba(0,0,0,0.1)",
            fontSize: 26,
            margin: "0 auto"
          }}
          title="Details"
          bordered={false}
        >
          <Space direction="vertical">
            <Text style={{ fontSize: 22 }} keyboard strong>
              Origin:{" "}
              <Text style={{ fontSize: 18 }}> {character?.origin?.name}</Text>
            </Text>
            <Text style={{ fontSize: 22 }} keyboard strong>
              Location:{" "}
              <Text style={{ fontSize: 18 }}> {character?.location?.name}</Text>
            </Text>
            <Text style={{ fontSize: 22 }} keyboard strong>
              Gender: <Text style={{ fontSize: 18 }}> {character?.gender}</Text>
            </Text>
            <Text style={{ fontSize: 22 }} keyboard strong>
              Species:{" "}
              <Text style={{ fontSize: 18 }}> {character?.species}</Text>
            </Text>
            <Text style={{ fontSize: 22 }} keyboard strong>
              Status: <Text style={{ fontSize: 18 }}> {character?.status}</Text>
            </Text>
            <Text style={{ fontSize: 22 }} keyboard strong>
              Episodes:{" "}
              <Text style={{ fontSize: 18 }}>
                {" "}
                {character?.episode?.length}
              </Text>
            </Text>
          </Space>
        </Card>
      </Space>
    </>
  );
}

function Show() {
  return (
    <App>
      <ShowPage />
    </App>
  );
}
export default Show;
